package com.example.risk.dao; import jakarta.enterprise.context.ApplicationScoped; import org.neo4j.driver.*; import java.util.*; @ApplicationScoped public class GraphQueries{private final Driver driver; public GraphQueries(Driver d){this.driver=d;} public boolean domainExists(String fqdn){try(Session s=driver.session()){return s.run("MATCH (d:Domain{fqdn:$f}) RETURN d",Map.of("f",fqdn)).hasNext();}}}